tinymce.PluginManager.add('imagebrowse', function(editor, url) {
	editor.addButton('imagebrowse', {
		tooltip: 'browse image',
		image : tinymce.baseURL + '/plugins/imagebrowse/icons/image-gallery.png',
		onclick: function() {
			editor.windowManager.open({
			title: '瀏覽圖片',
			//change this route to the one that returns the '_image-dialog.blade.php' file as a view
			file : '/admin/gallery/browse',
			width : 700,
			height: 400,
			buttons: [
			{
				text: 'Close',
				onclick: 'close'
			}]
			});
		}
	});
});