tinymce.PluginManager.add('imageupload', function(editor, url) {
	editor.addButton('imageupload', {
		tooltip: 'Upload an image',
		image : tinymce.baseURL + '/plugins/imageupload/icons/folder-upload.png',
		onclick: function() {
			editor.windowManager.open({
			title: '上傳圖片',
			//change this route to the one that returns the '_image-dialog.blade.php' file as a view
			file : '/admin/upload',
			width : 360,
			height: 310,
			buttons: [
			{
				text: 'Close',
				onclick: 'close'
			}]
			});
		}
	});
});